import React, { useState } from "react";
import Hero from "./components/Hero";
import PatientForm from "./components/PatientForm";

function App() {
  const [file, setFile] = useState(null);

  const handleSubmit = async () => {
    if (!file) {
      alert("Please upload an image first!");
      return;
    }

    // mock API call
    alert(`Image "${file.name}" sent for analysis. Report will be generated!`);
  };

  return (
    <div className="min-h-screen bg-w flex flex-col items-center p-6">
      {/* Hero Section */}
      <Hero />

      {/* Patient Form Section */}
      <div className="w-full max-w-lg my-6">
        <PatientForm />
      </div>

      {/* Upload Section */}
      <div className="bg-white shadow-md rounded-2xl p-6 w-full max-w-lg text-center">
        <h1 className="text-2xl font-bold text-gray-800 mb-4">Image Upload</h1>

        <input
          type="file"
          accept="image/*"
          onChange={(e) => setFile(e.target.files[0])}
          className="block w-full mb-4 text-sm text-gray-600"
        />

        <button
          onClick={handleSubmit}
          className="bg-blue-600 text-white px-6 py-2 rounded-xl shadow hover:bg-blue-700 transition"
        >
          Upload & Analyze
        </button>
      </div>
    </div>
  );
}

export default App;
